import { useState, useEffect } from "react";
import { motion } from "framer-motion";

const testimonials = [
  {
    name: "Alice Johnson",
    role: "Software Engineer",
    text: "This service is amazing! It has transformed the way I work and I highly recommend it.",
    image: "data:image/jpeg;base64,"
  },
  {
    name: "Michael Smith",
    role: "Product Manager",
    text: "Super easy to use and the UI is beautiful. I love the experience!",
    image: "data:image/jpeg;base64,"
  },
  {
    name: "Sophia Brown",
    role: "UX Designer",
    text: "An absolute game-changer! The best tool I've used in a long time.",
    image: "data:image/jpeg;base64,"
  }
];

const Testimonial = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  // Auto-scroll feature
  useEffect(() => {
    const interval = setInterval(() => {
      nextTestimonial();
    }, 5000); // Change testimonial every 5 seconds
    return () => clearInterval(interval);
  }, [currentIndex]);

  const nextTestimonial = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % testimonials.length);
  };

  const prevTestimonial = () => {
    setCurrentIndex((prevIndex) => (prevIndex - 1 + testimonials.length) % testimonials.length);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 text-gray-900 p-6">
      <h1 className="text-4xl font-bold mb-6">What Our Clients Say</h1>

      {/* Testimonial Card */}
      <motion.div
        key={currentIndex}
        initial={{ opacity: 0, x: -50 }}
        animate={{ opacity: 1, x: 0 }}
        exit={{ opacity: 0, x: 50 }}
        transition={{ duration: 0.5 }}
        className="bg-white shadow-lg rounded-lg p-6 max-w-lg text-center"
      >
        <img src={testimonials[currentIndex].image} alt="User" className="w-20 h-20 mx-auto rounded-full mb-4" />
        <p className="text-lg italic">{testimonials[currentIndex].text}</p>
        <h3 className="text-xl font-semibold mt-3">{testimonials[currentIndex].name}</h3>
        <p className="text-sm text-gray-500">{testimonials[currentIndex].role}</p>
      </motion.div>

      {/* Navigation Buttons */}
      <div className="flex gap-4 mt-6">
        <button onClick={prevTestimonial} className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-md">← Prev</button>
        <button onClick={nextTestimonial} className="px-4 py-2 bg-gray-300 hover:bg-gray-400 rounded-md">Next →</button>
      </div>
    </div>
  );
};

export default Testimonial;
